mapper.insert:
  		INSERT INTO ORDER_TBL (ORDER_NO
  							 , USER_NO
  							 , TOTAL_PRICE
  							 , RECEIVE_DATE
  							 , RECEIVE_USER
  							 , RECEIVE_PHONE
  							 , ORDER_ADDRESS
  							 , POSTCODE
  							 , ORDER_STATUS
  							 , DELIVERY_STATUS
  							 , RECEIPT_ID
  							 , THUMBNAIL)
  		VALUES(SEQ_ORDNO.NEXTVAL
		     , #{userNo}
		     , #{totalPrice}
		     , #{receiveDate}
		     , #{receiveUser}
		     , #{receivePhone}
		     , #{orderAddress}
		     , #{postcode}
		     , #{orderStatus}
		     , #{deliveryStatus}
		     , #{receiptId}
		     , #{thumbnail}
		     )

mapper.select:
SELECT *
FROM ORDER_TBL
JOIN DETAIL_ORDER USING (ORDER_NO)
JOIN MEMBER USING(USER_NO)
WHERE PRODUCT_NO = #{productNo}
AND USER_NO = #{userNo}
