mapper.insert:
  		INSERT INTO SUB_PRODUCT(SUBPRODUCT_NO
		                , SUBPRODUCT_NAME
		                , SUB_PRICE
		                , SUBPRODUCT_DESCRIPTION
		                , SUBORIGIN_NAME
		                , SUBCHANGE_NAME)
		VALUES(SEQ_SPNO.NEXTVAL
		     , #{subProductName}
		     , #{subPrice}
		     , #{subProductDescription}
		     , #{subOriginName}
		     , #{subChangeName})

mapper.select:
SELECT *
FROM SUB_PRODUCT
WHERE SUBPRODUCT_NO = #{ subProductNo }

mapper.delete:
DELETE
FROM SUB_PRODUCT
WHERE SUBPRODUCT_NO = #{ subProductNo }

mapper.update:
UPDATE SUB_PRODUCT
   SET SUBPRODUCT_NAME = #{ subProductName },
   	   SUBPRODUCT_DESCRIPTION = #{ subProductDescription },
   	   SUB_PRICE = #{ subPrice }
 WHERE SUBPRODUCT_NO = #{ subProductNo }

mapper.insert:
  		INSERT INTO SUBSCRIBE(SUBSCRIBE_NO
		                , SUBPRODUCT_NO
		                , USER_NO
		                , SUB_LEVEL
		                , SUB_RECEIVER_USER
		                , SUB_RECEIVER_PHONE
		                , SUB_RECEIVER_POSTCODE
		                , DELIVER_AT
		                , DELIVER_TO
		                , DELIVER_STATUS
		                , RECEIPT_ID)
		VALUES(SEQ_SNO.NEXTVAL
		     , #{subProductNo}
		     , #{userNo}
		     , #{subLevel}
		     , #{subReceiverUser}
		     , #{subReceiverPhone}
		     , #{subReceiverPostcode}
		     , #{deliverAt}
		     , #{deliverTo}
		     , #{deliverStatus}
		     , #{receiptId})

mapper.select:
SELECT COUNT(DISTINCT RECEIPT_ID)
FROM SUBSCRIBE
WHERE USER_NO = #{userNo}
